<style lang="postcss">
	svg {
		@apply overflow-visible -ml-1 cursor-none pointer-events-none;
	}

	svg.interactive {
		@apply pointer-events-auto;
	}

	.lastname path {
		animation: colorChange 20s linear alternate infinite;
	}

	.dot {
		pointer-events: none;
	}

	@keyframes nameColorChange {
		0% {
			fill: var(--primary-color);
		}
		33% {
			fill: var(--accent-color);
		}
		66% {
			fill: var(--link-color);
		}
		100% {
			fill: var(--copy-color);
		}
	}

	svg circle {
		@apply fill-orange;
	}

	svg:hover circle {
		@apply fill-white dark:fill-white mix-blend-exclusion;
		transform: translateX(-3.5px) translateY(-2px);
	}

	.wrapper {
		@apply overflow-visible w-full h-full;
	}
</style>
